"use strict";(self["webpackChunktatsuta_shokudo"]=self["webpackChunktatsuta_shokudo"]||[]).push([[178],{8178:function(e){e.exports=JSON.parse('{"title":{"english":"Gyudon","foreign":"牛丼"},"source":"https://www.justonecookbook.com/yoshinoya-beef-bowl-gyudon/","time":{"amount":20,"unit":"m"},"servings":{"amount":4,"unit":"Servings"},"date":[26,12,2023],"tags":["Main","Donburi","Japanese"],"ingredients":[{"title":null,"notes":"If you cannot find sliced beef in your local grocery store, freeze a block of fresh chuck or rib eye for 1-2 hours and slice. After you slice, if the piece is too large then cut them in half.","contents":[{"name":"Onion","amount":1,"unit":null},{"name":"Green Onions","amount":2,"unit":null},{"name":"Dashi","amount":1,"unit":"c"},{"name":"Sake","amount":2,"unit":"tbsp"},{"name":"Mirin","amount":4,"unit":"tbsp"},{"name":"Sugar","amount":2,"unit":"tbsp"},{"name":"Soy Sauce","amount":4,"unit":"tbsp"},{"name":"Thinly Sliced Beef","amount":{"n":3,"d":2},"unit":"lb","note":"chuck or ribeye"},{"name":"Pickeled Ginger","amount":4,"unit":"tbsp","note":"Beni Shouga"}]}],"directions":[{"title":"","steps":["Gather all the ingredients.","Cut the onion into thinly slices and chop the green onion. Set aside.","Heat a large frying pan over medium-high heat and add dashi, sake, sugar, mirin, and soy sauce.",{"step":"Cover the lid and bring the sauce to boil. Once the sauce is boiling, add the sliced onions and spread them out. Cover and cook until tender.","note":"If you don’t cover the pan, the sauce will evaporate, so make sure you cover the lid."},"When the onion is tender, add the beef and cook until no longer pink. Skim off the foam or fat with a fine-mesh sieve.","Serve the meat and sauce over steamed rice.","Top with chopped green onion and pickled red ginger."]}],"notes":[{"title":"Alternative Version","content":"For a slight variation on the dish, add in a beaten egg to the meat right before serving."}]}')}}]);